mapper.update:
UPDATE transaction
payment_option=#{paymentOption:CHAR} , receiver_name=#{receiverName:VARCHAR}, receiver_phone=#{receiverPhone:VARCHAR}, demailaddr=#{divyAddr:VARCHAR}, dlvy_request=#{divyRequest:VARCHAR}, dlvy_date=#{divyDate:DATE}, tran_quantity=#{tranQuantity}
WHERE tran_no=#{tranNo}

mapper.sql:
SELECT * FROM transaction

	
		
		
			buyer_id like #{searchKeyword}
		
		
			receiver_name like #{searchKeyword}
		
		
			prod_no like #{searchKeyword}
		
	
	
		AND tran_status_code IN
		
			#{tranCode}
		
	


	ORDER BY tran_no ASC NULLS LAST


	
		ORDER BY buyer_id ASC NULLS LAST, tran_no
	
	
		ORDER BY tran_status_code ASC NULLS LAST, tran_no
